const blocks = $('自我介紹 child blocks').all().map(item => item.json);
const event = $('active').item.json;
const joinedUserId = event.source.userId;
const managerId = "U2a0a2c5054c4fa12b78a1d059411e39c";

const validBlocks = blocks.filter(b => b.content && b.content.trim() !== "");
let templateText = validBlocks.map(b => {
    if (b.type === 'bulleted_list_item') {
        return `• ${b.content}`;
    }
    return b.content;
}).join('\n');

if (!templateText) templateText = "自我介紹 (Intro Message Empty)";

// 建立 substitution 物件（使用 textV2 格式）
const substitution = {};

if (templateText.includes('{USER}')) {
    substitution.USER = {
        type: "mention",
        mentionee: {
            type: "user",
            userId: joinedUserId
        }
    };
}

if (templateText.includes('{MANAGER}')) {
    substitution.MANAGER = {
        type: "mention",
        mentionee: {
            type: "user",
            userId: managerId
        }
    };
}

const result = {
    type: "textV2",
    text: templateText
};

if (Object.keys(substitution).length > 0) {
    result.substitution = substitution;
}

return result;

const logic = $("Registration Logic Processor").item.json;
if (logic.error) {
  return { type: 'text', text: logic.error };
}

const participants = logic.finalParticipants || [];
const guestParticipants = participants.filter(p => p.type === 'guest');
const lines = [];

const grouped = [];
guestParticipants.forEach(p => {
    const baseName = p.name.replace(/朋友.*$/, '朋友').replace(/ \d+$/, '');
    if (grouped.length > 0 && grouped[grouped.length-1].base === baseName) {
        grouped[grouped.length-1].count++;
    } else {
        grouped.push({ base: baseName, name: p.name, type: p.type, count: 1 });
    }
});

let currentCount = 0;
grouped.forEach((g, i) => {
    currentCount++;
    if (g.count > 1) {
        lines.push(`${currentCount}. ${g.base} +${g.count}`);
    } else {
        lines.push(`${currentCount}. ${g.name}`);
    }
});

// Pad with empty numbered slots
const maxSlots = logic.maxSlots || 0;
const usedSlots = guestParticipants.length; 
// Logic note: lines printed above correspond to groups. 
// Standard requirement: One line per slot? No, commonly "1. Name", "2. Name".
// If +2, it might take 1 line but 2 slots? 
// Original code: lines.push(...) for each GROUP. 
// If I have "Name +2", that is 2 slots.
// But the display requirement usually lists slots physically or just available count.
// User requested: "numbered list of empty slots".
// Assuming we continue numbering from where we left off.

// Let's refine the numbering to match slots.
// Actually, usually users want to see:
// 1. A
// 2. B
// 3. 
// 4.

// Re-doing the loop to be simpler for this requirement:
// Not grouping +2 for the list? 
// User prompt said: "duplicate zero-day signups needed to be displayed with a + separator". 
// So "Name+2" is likely one entry in the list?
// If "Name+2" (2 people) takes 2 slots.
// Does it show as:
// 1. Name+2
// 3. 
// OR
// 1. Name+2
// 2. Name+2 (implied)
// 3.

// Let's stick to the previous grouping logic for now, but just append empty numbers.
// If "Name+2" takes 2 slots, but occupies 1 line "1. Name+2". 
// The next available slot number should probably start after the *used* slots.
// total used = guestParticipants.length.
// So if 2 guests, next empty is 3.

const usedCount = guestParticipants.length;
const startEmpty = usedCount + 1;

for (let i = startEmpty; i <= maxSlots; i++) {
    lines.push(`${i}.`);
}

const remaining = Math.max(0, logic.maxSlots - guestParticipants.length);
const leaveNames = logic.finalLeaveNames || [];
const textParts = [
  `${logic.eventDate}`,
  `零打名額 ${logic.maxSlots} 人 | ${logic.fee}/人`,
  ...lines,
  `剩餘名額： ${remaining} 人`
];

if (leaveNames.length > 0) {
    textParts.push(`請假：${leaveNames.join('、')}`);
}

textParts.push('', `若要報名請輸入 @Dobby +1`);

const text = textParts.join('\n');

return { type: 'text', text };

const aggregate = $("Aggregate Target").item.json;
const quarterInfo = $("取得該季資訊").first().json;
const metadata = $("metadata").first().json;
const allEvents = $("取得打球日").all().map(item => item.json);
const peopleList = $("Get People Next").all().map(item => item.json);

// Robust property getters
const getDate = (item) => (item.properties?.['時間']?.date?.start || (typeof item.property_時間 === 'string' ? item.property_時間 : item.property_時間?.start));
const getRelationIds = (item, key) => (item.properties?.[key]?.relation?.map(r => r.id) || item[`property_${key.toLowerCase()}`] || []);
const getMultiSelect = (item, key) => (item.properties?.[key]?.multi_select?.map(s => s.name) || item[`property_${key.toLowerCase()}`] || []);
const getNumber = (item, key) => (item.properties?.[key]?.number || item[`property_${key.toLowerCase()}`] || 0);
const getName = (item) => (item.properties?.['Name']?.title?.[0]?.plain_text || item.property_name || item.name);

const targetDate = metadata.nextSaturdayDateText;
const eventPage = allEvents.find(page => {
  const startTime = getDate(page);
  return startTime && startTime.startsWith(targetDate);
});

if (!eventPage) {
  throw new Error(`找不到 ${targetDate} 的活動頁面`);
}

const peopleMap = new Map();
peopleList.forEach(p => peopleMap.set(p.id, p));

const command = aggregate.command;
const targetPersonId = aggregate.targetPersonId;
const targetPersonName = aggregate.targetPersonName;
const actorIsAdmin = aggregate.actorIsAdmin;

const seasonMembers = getRelationIds(quarterInfo, '報名人');
const isSelfSeasonPlayer = targetPersonId && seasonMembers.includes(targetPersonId);

const maxCourts = getNumber(eventPage, '場地數') || getNumber(quarterInfo, '場地數') || 0;
const totalCapacity = maxCourts * 7;
const leaveList = getRelationIds(eventPage, '請假人');
const guestList = getMultiSelect(eventPage, '零打'); // Changed to Multi-select (strings)
const seasonTotal = seasonMembers.length;

let updateType = null;
let updateData = null;
let error = null;

// Check Parser Error or Admin Permission
if (aggregate.parserError) {
    error = aggregate.parserError;
} else if (!aggregate.isSelf && !aggregate.actorIsAdmin) {
    error = '你才不是管理員';
}

// Helper to resolve display name (e.g., 修_Kevin -> 許文修)
const resolveDisplayName = (rawBaseName) => {
    if (rawBaseName === targetPersonName && targetPersonId) {
        return getName(peopleMap.get(targetPersonId) || {}) || rawBaseName;
    }
    return rawBaseName;
};

if (!error) {
    try {
      if (command === '假' || command === '銷假') {
        if (!isSelfSeasonPlayer) {
          error = '只有季打球員才能請假/銷假喔';
        } else {
          updateType = 'leave';
          if (command === '假') {
            if (leaveList.includes(targetPersonId)) {
              error = `${targetPersonName} 已經請過假囉`;
            } else {
              updateData = [...leaveList.map(id => ({ id })), { id: targetPersonId }];
            }
          } else {
            if (!leaveList.includes(targetPersonId)) {
              error = `${targetPersonName} 本來就沒有請假喔`;
            } else {
              updateData = leaveList.filter(id => id !== targetPersonId).map(id => ({ id }));
            }
          }
        }
      } else if (command === '+1' || command === '+2' || command === '-1') {
        updateType = 'guest';
        const count = (command === '+2') ? 2 : 1;

        // Base name for internal tracking vs display matching
        let baseName = targetPersonName;
        if (isSelfSeasonPlayer) baseName = `${targetPersonName}朋友`;

        // Display name based on Personnel List registration
        const baseDisplayName = resolveDisplayName(baseName);

        // Current attendance calc (pre-update for validation)
        const PreCalcLeaveLen = leaveList.length;
        const PreCalcGuestLen = guestList.length;
        const PreCalcAttendance = seasonTotal - PreCalcLeaveLen + PreCalcGuestLen;

        if (command.startsWith('+')) {
          if (PreCalcAttendance + count > totalCapacity && !actorIsAdmin) {
            error = '名額已滿囉，下次請早點喊聲！';
          } else {
            const newGuests = [...guestList];
            for (let i = 0; i < count; i++) {
               let attempt = 1;
               while (true) {
                 const candidate = attempt === 1 ? baseDisplayName : `${baseDisplayName}+${attempt}`;
                 if (!newGuests.includes(candidate)) {
                   newGuests.push(candidate);
                   break;
                 }
                 attempt++;
               }
            }
            updateData = newGuests; // Array of strings for Multi-select
          }
        } else {
          const matchedIndices = guestList.map((name, index) => ({ name, index }))
            .filter(g => g.name === baseDisplayName || g.name.startsWith(`${baseDisplayName}+`));

          if (matchedIndices.length === 0) {
            error = `找不到 ${targetPersonName} 的報名紀錄`;
          } else {
            matchedIndices.sort((a, b) => {
                const getNum = (s) => {
                    if (s === baseDisplayName) return 1;
                    const m = s.match(/\+(\d+)$/);
                    return m ? parseInt(m[1]) : 1;
                };
                return getNum(b.name) - getNum(a.name);
            });
            updateData = guestList.filter((_, i) => i !== matchedIndices[0].index);
          }
        }
      }
    } catch (e) {
      error = '我腦袋壞去，突然不知道處理這些事情了...';
    }
}

// === Post-Update Logic ===
// Determine final state for reply
const finalLeaveList = (updateType === 'leave' && !error && updateData) ? updateData.map(d => d.id) : leaveList;
const finalGuestList = (updateType === 'guest' && !error && updateData) ? updateData : guestList;

// Corrected guestCapacity calculation: Total Capacity - (Season Members - Final Absentees)
const guestCapacity = Math.max(0, totalCapacity - (seasonTotal - finalLeaveList.length));

// Calculate final participants list
const finalParticipants = [];
seasonMembers.forEach(id => {
  if (!finalLeaveList.includes(id)) {
    finalParticipants.push({
      name: getName(peopleMap.get(id) || {}) || "未知球員",
      type: 'season'
    });
  }
});

finalGuestList.forEach(name => {
  finalParticipants.push({
    name: name || "未知朋友",
    type: 'guest'
  });
});

return {
  parser: aggregate,
  eventPageId: eventPage.id,
  eventDate: targetDate,
  maxSlots: guestCapacity,
  fee: getNumber(quarterInfo, '零打費用'),
  updateType,
  updateData,
  error,
  isSeasonPlayer: isSelfSeasonPlayer,
  finalParticipants,
  finalLeaveNames: finalLeaveList.map(id => {
      const p = peopleMap.get(id);
      return p ? getName(p) : "未知球員";
  })
};

const parser = $("Registration Parser").item.json;
const resolve = $("Resolve Registration Target").first().json;

// Robust property getters
const getRegisteredNameId = (item) => (item.properties?.['Registered name']?.relation?.[0]?.id || item.property_registered_name?.[0]);
const getCustomName = (item) => (item.properties?.['Custom Name']?.rich_text?.[0]?.plain_text || item.property_custom_name);
const getUserId = (item) => (item.properties?.['user_id']?.title?.[0]?.plain_text || item.property_user_id);
const getName = (item) => (item.properties?.['Name']?.title?.[0]?.plain_text || item.property_name || item.name);

let targetPersonId = null;
let targetPersonName = "Unknown";

try {
  if (resolve.type === 'self') {
    targetPersonId = resolve.targetPersonId;
    targetPersonName = resolve.targetName;
  } else if (resolve.type === 'mention') {
    const targetUser = $("Get Target User (Mention)").item.json;
    targetPersonId = getRegisteredNameId(targetUser);
    targetPersonName = getCustomName(targetUser) || getUserId(targetUser);
  } else {
    const targetPerson = $("Get Target Person (By Name)").item.json;
    targetPersonId = targetPerson.id;
    targetPersonName = getName(targetPerson) || parser.targetName;
  }
} catch (e) {
  // If lookup failed, we still have the name from parser if provided
  targetPersonName = parser.targetName || "Unknown";
}

return {
  ...parser,
  actorIsAdmin: resolve.isAdmin,
  targetPersonId,
  targetPersonName
};

const parser = $("Registration Parser").item.json;
const actor = $input.item.json;

// Robust property getters
const getRegisteredNameId = (item) => (item.properties?.['Registered name']?.relation?.[0]?.id || item.property_registered_name?.[0]);
const getCustomName = (item) => (item.properties?.['Custom Name']?.rich_text?.[0]?.plain_text || item.property_custom_name);
const getUserId = (item) => (item.properties?.['user_id']?.title?.[0]?.plain_text || item.property_user_id);
const getIsAdmin = (item) => (item.properties?.['is_admin']?.checkbox ?? item.property_is_admin ?? false);

if (parser.isSelf) {
  return [
    {
      json: {
        type: 'self',
        targetPersonId: getRegisteredNameId(actor),
        targetName: getCustomName(actor) || getUserId(actor),
        isAdmin: getIsAdmin(actor) === true,
        branch: 0
      }
    }
  ];
}

if (parser.targetUserId) {
  return [
    {
      json: {
        type: 'mention',
        targetUserId: parser.targetUserId,
        isAdmin: getIsAdmin(actor) === true,
        branch: 1
      }
    }
  ];
}

return [
  {
    json: {
      type: 'name',
      targetName: parser.targetName,
      isAdmin: getIsAdmin(actor) === true,
      branch: 2
    }
  }
];

const event = $("active").item.json;
const msg = event.message;
// Remove @Dobby case-insensitive, then trim
const text = msg.text.replace(/@Dobby/i, "").trim();

const commandRegex = /(\+1|\+2|-1|假|銷假)/;
const match = text.match(commandRegex);
const command = match ? match[1] : null;

let targetName = null;
let targetUserId = null;
let parserError = null;

if (command) {
  const rawTarget = text.replace(commandRegex, "").trim();
  if (rawTarget) {
    if (rawTarget.startsWith("@")) {
      const mentions = msg.mention?.mentions || [];
      const cleanTarget = rawTarget.substring(1);
      if (mentions.length > 0) {
        targetUserId = mentions[0].userId;
        targetName = cleanTarget;
      } else {
        targetName = cleanTarget;
      }
    } else {
      parserError = "指令格式錯誤：指定對象需使用 @Name";
      targetName = rawTarget;
    }
  }
}

return {
  command,
  targetName,
  targetUserId,
  parserError,
  originalText: msg.text,
  isSelf: !targetName && !targetUserId && !parserError,
  actorUserId: event.source.userId
};

// Get original event
const originalEvent = $("is command").item.json;

// Get user query results from Notion
const users = $input.all();
const hasValidUser = users.length > 0 && users[0].json && users[0].json.id;

let isAdmin = false;
if (hasValidUser) {
  const user = users[0].json;
