const managerId = "U2a0a2c5054c4fa12b78a1d059411e39c";

const validBlocks = blocks.filter(b => b.content && b.content.trim() !== "");
let templateText = validBlocks.map(b => {
    if (b.type === 'bulleted_list_item') {
        return `• ${b.content}`;
    }
    return b.content;
}).join('\n');

if (!templateText) templateText = "歡迎！ (Welcome Message Empty)";

// 建立 substitution 物件（使用 textV2 格式）
const substitution = {};

if (templateText.includes('{NEW_FRIEND}')) {
    substitution.NEW_FRIEND = {
        type: "mention",
        mentionee: {
            type: "user",
            userId: joinedUserId
        }
    };
}

if (templateText.includes('{MANAGER}')) {
    substitution.MANAGER = {
        type: "mention",
        mentionee: {
            type: "user",
            userId: managerId
        }
    };
}

const result = {
    type: "textV2",
    text: templateText
};

if (Object.keys(substitution).length > 0) {
    result.substitution = substitution;
}

return result;

const userMessage = $('Split Out').first().json.message.text || '';
const quoteToken = $('Split Out').first().json.message.quoteToken;
const replyItems = $input.all().map(item => item.json);

// Robust property getters
const getName = (item) => (item.properties?.['name']?.title?.[0]?.plain_text || item.name || '');
const getReply = (item) => (item.properties?.['reply']?.rich_text?.[0]?.plain_text || item.property_reply || '');

// 檢查使用者訊息是否包含任一 name
for (const item of replyItems) {
    const name = getName(item);
    if (name && userMessage.includes(name)) {
        return {
            type: 'text',
            text: getReply(item),
            quoteToken: quoteToken
        };
    }
}

// 如果沒有匹配，不輸出任何東西（不回覆）
return [];

const blocks = $('付款資訊 child blocks').all().map(item => item.json);
const quoteToken = $('Split Out').first().json.message.quoteToken;

const validBlocks = blocks.filter(b => b.content && b.content.trim() !== "");
