function getNextSaturday () {
  const now = DateTime.now(); 
  const currentWeekday = now.weekday; 
  const daysUntilSaturday = ((6 - currentWeekday + 7) % 7) || 7; 
  const nextSaturday = now.plus({ days: daysUntilSaturday });
  return nextSaturday
}

function getNextSaturdayQuarter() {
  const nextSaturday = getNextSaturday()
  const year = nextSaturday.year;
  const quarter = nextSaturday.quarter; 
  return `${year}-Q${quarter}`; 
}

return {
  targetQuarter: getNextSaturdayQuarter()
}

const blocks = $('最新公告 child blocks').all().map(item => item.json);
const seasonPage = $('Get Season Info for Announce').first().json;
const calendarPages = $('Get Calendar for Announce').all().map(item => item.json);
const peopleList = $('Get People for Announce').all().map(item => item.json);

const quoteToken = $('Split Out').first().json.message.quoteToken;

// Validate Data
if (!seasonPage) {
    return {
        type: "text",
        text: "❌ 找不到本季季度資料",
        quoteToken: quoteToken
    };
}

// --- Helper: People Map ---
const peopleMap = new Map();
peopleList.forEach(p => peopleMap.set(p.id, p));

function getPersonName(id) {
    const p = peopleMap.get(id);
    if (!p) return "Unknown";
    return p.properties['Name']?.title?.[0]?.plain_text || p.name || "Unknown";
}

// --- Prepare Variables ---

// {SEASON}
const SEASON = seasonPage.properties['季租時段']?.title?.[0]?.plain_text || "Unknown-Quarter";

// {FROM_TO_MONTH}
let FROM_TO_MONTH = "";
if (SEASON.includes("Q1")) FROM_TO_MONTH = "1~3月";
else if (SEASON.includes("Q2")) FROM_TO_MONTH = "4~6月";
else if (SEASON.includes("Q3")) FROM_TO_MONTH = "7~9月";
else if (SEASON.includes("Q4")) FROM_TO_MONTH = "10~12月";

// {TOTAL_PEOPLE}
const TOTAL_PEOPLE = seasonPage.properties['報名人數']?.formula?.number || 0;

// {LIST_ALL_PEOPLE}
const relations = seasonPage.properties['報名人']?.relation || [];
const LIST_ALL_PEOPLE = relations.map(r => getPersonName(r.id)).join("、");

// {PRICE_PER_PERSON_FOR_SEASON}
const PRICE_PER_PERSON_FOR_SEASON = seasonPage.properties['每人平均場租']?.formula?.number || 0;

// {PRICE_PER_PERSON_FOR_ONCE}
const PRICE_PER_PERSON_FOR_ONCE = seasonPage.properties['零打費用']?.number || 0;

// {COURT_COUNT}
const COURT_COUNT = seasonPage.properties['場地數']?.number || 0;

// {TOTAL_PRICE}
const TOTAL_PRICE = seasonPage.properties['場租總金額']?.formula?.number || 0;

// {LOCATION}
const LOCATION = seasonPage.properties['地點']?.rich_text?.[0]?.plain_text || 
               seasonPage.properties['地點']?.select?.name || "未知地點";

// Filter active play dates (exclude paused)
const activeCalendarPages = calendarPages.filter(p => {
    const type = p.properties['類型']?.select?.name;
    return type !== '暫停';
});

// {WEEK_COUNTS}
