function getQuarter(date) {
  const year = date.year;
  const quarter = date.quarter;
  return `${year}-Q${quarter}`;
}

// Use $json directly in runOnceForEachItem mode
const text = $json.message.text;
let arg = text.replace(/^@Dobby\s+(participants|people|報名人)\s*/i, '').trim();

let targetQuarter;
if (arg) {
    targetQuarter = arg.toUpperCase();
} else {
    targetQuarter = getQuarter(DateTime.now());
}

return {
    targetQuarter
}

const quarterInfo = $('取得該季資訊').first().json;
const metadata = $('metadata').first().json;

// Robust property getters
const getNumber = (item, key) => (item.properties?.[key]?.number || item[`property_${key.toLowerCase()}`] || 0);
const getFormulaNumber = (item, key) => (item.properties?.[key]?.formula?.number || item[`property_${key.toLowerCase()}`] || 0);
const getName = (item) => (item.properties?.['Name']?.title?.[0]?.plain_text || item.property_name || item.name);
const getDate = (item) => (item.properties?.['時間']?.date?.start || (typeof item.property_時間 === 'string' ? item.property_時間 : item.property_時間?.start));

// 從不同來源彙整資訊
const quarterData = {
  fee: getNumber(quarterInfo, '零打費用') || metadata.price,
  memberCount: getFormulaNumber(quarterInfo, '報名人數') || 0,
  defaultCourts: metadata.courts // Fallback to metadata if not in Quarter DB
};

// 取得當前節點 (Event) 的資料
const targetDate = metadata.nextSaturdayDateText;
const allEvents = $('取得打球日').all().map(item => item.json);

const eventPage = allEvents.find(page => {
  const startTime = getDate(page);
  return startTime && startTime.startsWith(targetDate);
});

if (!eventPage) {
  return [
    {
      "type": "text",
      "text": `⚠️ 找不到 ${targetDate} 的打球日活動。\n請確認 Notion 行事曆是否已建立該日期的頁面。`
    }
  ];
}

// Get People Map
const peopleData = $('Get People Next').all().map(item => item.json);
const peopleMap = new Map();
peopleData.forEach(p => peopleMap.set(p.id, p));

const eventData = {
  type: eventPage.properties?.['類型']?.select?.name || eventPage.property_類型,
  timeStart: getDate(eventPage),
  courtOverride: getNumber(eventPage, '場地數'),
  absenteesRelations: eventPage.properties?.['請假人']?.relation || eventPage.property_請假人 || [],
  absenteesCount: (eventPage.properties?.['請假人']?.relation?.length ?? eventPage.property_請假人?.length ?? 0)
};

// 1. 檢查是否暫停
if (eventData.type && eventData.type.includes('暫停')) {
  return [
    {
      "type": "text",
      "text": `📅 ${targetDate}\n\n🛑 本週打球暫停 (Suspended)\n休息是為了走更長遠的路！`
    }
  ];
}

// 2. 計算邏輯
// 場地數：優先使用 Event Override，否則使用 Quarter Default (or Metadata default)
const finalCourts = eventData.courtOverride || quarterData.defaultCourts;
const courtsDensity = 7; // 每面場地 7 人

// 總請假人數：Event 紀錄的請假人 + 指令參數額外扣除的 (metadata.offCounts)
const manualOffCounts = Number(metadata.offCounts) || 0;
const totalAbsentees = eventData.absenteesCount + manualOffCounts;

// Resolve Names
const absenteeNames = eventData.absenteesRelations.map(r => {
    const id = r.id || r;
    const person = peopleMap.get(id);
    return getName(person || {});
}).filter(n => n);

const leaveString = absenteeNames.length > 0 ? `請假：${absenteeNames.join(', ')}` : `請假：無`;

// 應到人數 (季租 - 請假)
const expectedMembers = quarterData.memberCount - totalAbsentees;

// 零打名額 (Total Slots - Expected Members)
const totalSlots = finalCourts * courtsDensity;
const availableSlots = Math.max(0, totalSlots - expectedMembers);

// 3. 格式化輸出
const dateStr = eventData.timeStart ? eventData.timeStart.split('T')[0] : targetDate;

function generateNumberedListString(number) {
  if (number <= 0) return '(無名額)';
  const lines = [];
  for (let i = 1; i <= number; i++) {
    lines.push(`${i}.`);
  }
  return lines.join('\n');
}

return [
  {
    "type":"text",
    "text":[
      `${dateStr} 不能到請喊聲`,
      leaveString,
      `場地：${finalCourts} 面`,
      `應到：${expectedMembers} 人`,
      `零打名額：${availableSlots}人 $${quarterData.fee}/人`
    ].join('\n')
  },
  {
    "type":"text",
    "text":[
      `${dateStr}`,
      `零打名額 ${availableSlots} 人 | $${quarterData.fee}/人`, 
      `${generateNumberedListString(availableSlots)}`
    ].join('\n')
  }
];

function getNextSaturday () {
