function parseQueryStringManualNoInnerReturn(commandString) {
  const queryIndex = commandString.indexOf('?');

  // ç¬¬ä¸€å±¤ returnï¼šè™•ç†æ²’æœ‰ '?' æˆ– '?' åœ¨çµå°¾çš„æƒ…æ³
  if (queryIndex === -1 || queryIndex === commandString.length - 1) {
    return {};
  }

  const queryString = commandString.substring(queryIndex + 1);

  // ç¬¬ä¸€å±¤ returnï¼šè™•ç† '?' å¾Œé¢æ²’æœ‰å…§å®¹çš„æƒ…æ³
  if (!queryString) {
    return {};
  }

  const params = {}; // åˆå§‹åŒ–çµæœç‰©ä»¶
  const pairs = queryString.split('&');

  // ä½¿ç”¨ forEach éæ­·
  pairs.forEach(pair => {
    // *** ä¿®æ”¹é»ï¼šå°‡åŸæœ¬çš„ return æ”¹æˆ if åˆ¤æ–· ***
    // åªæœ‰ç•¶ pair ä¸æ˜¯ç©ºå­—ä¸²æ™‚ï¼Œæ‰åŸ·è¡Œå¾ŒçºŒçš„è§£æé‚è¼¯
    if (pair) {
      let key = pair;
      let value = '';
      const eqIndex = pair.indexOf('=');

      if (eqIndex !== -1) {
        key = pair.substring(0, eqIndex);
        value = pair.substring(eqIndex + 1);
      }

      try {
        const decodedKey = decodeURIComponent(key.replace(/\+/g, ' '));
        const decodedValue = decodeURIComponent(value.replace(/\+/g, ' '));
        params[decodedKey] = decodedValue;
      } catch (e) {
        console.warn(`Failed to decode pair: "${pair}". Key: "${key}", Value: "${value}"`, e);
        params[key.replace(/\+/g, ' ')] = value.replace(/\+/g, ' ');
      }
      // *** åŸæœ¬åœ¨é€™è£¡çš„éš±å¼çµæŸï¼ˆæˆ–ä¹‹å‰çš„ returnï¼‰ç¾åœ¨æ˜¯ if å¡Šçš„çµæŸ ***
    }
    // *** å¦‚æœ pair æ˜¯ç©ºå­—ä¸²ï¼Œå‰‡é€™å€‹ forEach è¿­ä»£ä»€éº¼éƒ½ä¸åš ***
  });

  // ç¬¬ä¸€å±¤ returnï¼šå›å‚³æœ€çµ‚çš„çµæœç‰©ä»¶
  return params;
}

function getNextSaturday () {
  const now = DateTime.now(); 
  const currentWeekday = now.weekday; 
  const daysUntilSaturday = ((6 - currentWeekday + 7) % 7) || 7; 
  const nextSaturday = now.plus({ days: daysUntilSaturday });
  return nextSaturday
}

function getNextSaturdayQuarter() {
  const nextSaturday = getNextSaturday()
  const year = nextSaturday.year;
  const quarter = nextSaturday.quarter; 
  return `${year}-Q${quarter}`; 
}

function getNextSaturdayDateText () {
  const nextSaturday = getNextSaturday()
  return nextSaturday.toFormat('yyyy-MM-dd')
}

